const baseUrl = 'http://127.0.0.1:3000/departments';

export async function addDepartment(department) {
    try {
        const response = await fetch(`${baseUrl}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(department),
        });
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('POST request error:', error);
        throw error;
    }
}

export async function getCourses() {
    try {
        const departmentCode = "65315c538dd6286039221d13";
        const response = await fetch(`${baseUrl}/${departmentCode}/courses`);
        const data = await response.json();
        console.log(data);
        return data.courses;
    } catch (error) {
        console.error('GET request error:', error);
        throw error;
    }
}

export async function addCourse(newCourse) {
    try {
        const departmentCode = "65315c538dd6286039221d13";
        const response = await fetch(`${baseUrl}/${departmentCode}/courses`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newCourse),
        });
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('POST request error:', error);
        throw error;
    }
}

export async function updateCourse(courseCode, updatedName) {
    try {
        const departmentCode = "65315c538dd6286039221d13";
        const response = await fetch(`${baseUrl}/${departmentCode}/courses/${courseCode}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name: updatedName }),
        });
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('PATCH request error:', error);
        throw error;
    }
}

export async function deleteCourse(courseCode) {
    try {
        const departmentCode = "65315c538dd6286039221d13";
        const response = await fetch(`${baseUrl}/${departmentCode}/courses/${courseCode}`, {
            method: 'DELETE',
        });
    } catch (error) {
        console.error('DELETE request error:', error);
        throw error;
    }
}
