const baseUrl = 'http://localhost:3000/departments';
const departmentCode = "6531f8192ce0d8dd89faee19";

export async function addDepartment(department) {
    try {
        const response = await fetch(`${baseUrl}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(department),
        });
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('POST request error:', error);
        throw error;
    }
}

export async function getCourses() {
    try {
        const response = await fetch(`${baseUrl}/${departmentCode}/courses`);
        const data = await response.json();
        console.log(data);
        return data;
    } catch (error) {
        console.error('GET request error:', error);
        throw error;
    }
}

export async function addCourse(newCourse) {
    try {
        const response = await fetch(`${baseUrl}/${departmentCode}/courses`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newCourse),
        });
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('POST request error:', error);
        throw error;
    }
}

export async function updateCourse(course) {
    try {
        console.log(course)
        const response = await fetch(`${baseUrl}/${departmentCode}/courses/${course._id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(course),
        });
        const data = await response.json();
        return data;
    } catch (error) {
        console.error('PATCH request error:', error);
        throw error;
    }
}

export async function deleteCourse(courseCode) {
    try {
        console.log(courseCode);
        const response = await fetch(`${baseUrl}/${departmentCode}/courses/${courseCode}`, {
            method: 'DELETE',
        });
        return response;
    } catch (error) {
        console.error('DELETE request error:', error);
        throw error;
    }
}
