import { AntDesign } from '@expo/vector-icons';
import React, { useState } from 'react';
import { View, TouchableOpacity } from 'react-native';
import { TouchableHighlight } from 'react-native-gesture-handler';
function TouchableStar({ index,currentIndex,setCurrentIndex }) {
  const pressed = () => {
    setCurrentIndex(index);
    if(index<=currentIndex){
      setCurrentIndex(index-1);
    }else{
      setCurrentIndex(index)
    }
  }
  return (
    <TouchableHighlight onPress={pressed}>
      <AntDesign name='star' size={30} color={index <= currentIndex ? "orange" : "gray"} />
    </TouchableHighlight>
  )
}
const StarRating = ({rasetRating}) => {
  let stars = [...Array(5)];
  const [currentIndex, setCurrentIndex] = useState(-1);
  return (
    <View style={{ flexDirection: "row", justifyContent: "center" }}>
      {
        stars.map((_, i) => {
          return <TouchableStar key={i} index={i} currentIndex={currentIndex} setCurrentIndex={setCurrentIndex} />;
        })
      }
    </View>
  )
};

export default StarRating;
