import 'react-native-gesture-handler';
import React, { useEffect } from 'react';
import { StyleSheet, View } from 'react-native';
import Constants from 'expo-constants';
import { NavigationContainer } from '@react-navigation/native';
import About from './components/About';
import Course from './components/Course';
import CourseDetails from './components/CourseDetails';
import AddReview from './components/AddReview'
import CourseList from './components/CoursesList'
import Context from './Context';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import CoursesList from './components/CoursesList';
const Stack = createStackNavigator();
const Tab = createMaterialBottomTabNavigator();


function CoursesStack() {
  return (
    <Stack.Navigator>
      <Stack.Screen name="coursesList" component={CourseList} />
      <Stack.Screen name="courseDetails" component={CourseDetails} />
      <Stack.Screen name="addReview" component={AddReview} />
    </Stack.Navigator>
  );
}

export default function App() {
  const [cachedData, setCachedData] = React.useState([]); // 初始化为适当的初始数据

  useEffect(() => {
    const loadDataFromCache = async () => {
      try {
        const data = await AsyncStorage.getItem('cachedData');
        if (data !== null) {
          setCachedData(JSON.parse(data));
        }else{
          const data = await AsyncStorage.setItem('cachedData',JSON.stringify(CoursesList.data))
          if(data){

          }
        }
      } catch (error) {
        console.error('Error loading data from cache:', error);
      }
    };

    loadDataFromCache();
  }, []);

  useEffect(() => {
    const saveDataToCache = async () => {
      try {
        await AsyncStorage.setItem('cachedData', JSON.stringify(cachedData));
      } catch (error) {
        console.error('Error saving data to cache:', error);
      }
    };

    saveDataToCache();
  }, [cachedData]);

  return (
    <Context.Provider value={{ cachedData, setCachedData }}>
      <NavigationContainer>
        <Tab.Navigator>
          <Tab.Screen name="Courses" component={CoursesStack} />
          <Tab.Screen name="About" component={About} />
        </Tab.Navigator>
      </NavigationContainer>
    </Context.Provider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: Constants.statusBarHeight,
  },
});
