import { StyleSheet, View, Text, TextInput, Button, ActivityIndicator } from 'react-native';
import StarRating from './StarsComponent';
import { useState, useContext } from 'react';
import Context from '../Context';

const AddReview = ({ route }) => {
  const { data } = route.params;
  const [rating, setRating] = useState(0);
  const [reviewText, setReviewText] = useState('');
  const { cachedData, setCachedData } = useContext(Context);


  const [submitting, setSubmitting] = useState(false);
  const toRating = () => {
    const submitReview = () => {
      setSubmitting(true);
      setTimeout(() => {
        setSubmitting(false);
      }, 1000);
    }
  };
  return (
    <View style={styles.root}>
      <Text style={styles.addReview}>Add Review</Text>
      <TextInput style={styles.input}></TextInput>
      <Text style={styles.rating}>Your Rating</Text>
      <View>
        <StarRating rating={rating} onStarPress={handleStarPress} />
      </View>
      <TextInput
        placeholder="Review"
        onChangeText={handleChangeText}
        value={reviewText}
        style={styles.input}
        multiline numberOfLines={4}
      ></TextInput>
      {submitting && <ActivityIndicator size="large" />}
      <Button style={styles.submitButtonText} title="Submit" onPress={toRating} />
    </View>
  );
};

const styles = StyleSheet.create({
  root: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: 20,
    justifyContent: 'center',
    alignItems: 'center'
  },
  button: {
    paddingHorizontal: 10,
  },
  addReview: {
    fontSize: 25,
    color: '#444',
    textAlign: 'center',
    margin: 20,
  },
  input: {
    padding: 10,
    marginVertical: 10,
    marginHorizontal: 20,
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 3,
  },
  rating: {
    fontSize: 20,
    color: 'grey',
    textAlign: 'center',
    marginVertical: 40,
  },
  stars: {
    marginBottom: 80,
    flexDirection: 'row',
    justifyContent: 'center',
  },
  starButton: {
    padding: 5,
  },
  submitButton: {
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: '#0066cc',
    borderRadius: 4,
    marginVertical: 10,
    marginHorizontal: 20,
  },
  submitButtonText: {
    fontSize: 18,
    color: '#ffffff',
    textAlign: 'center',
  },
});

export default AddReview;
