import { StyleSheet, View, Text, TextInput, Button } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import Stars from './Stars';
import StarRating from './StarsComponent';
import { useState } from 'react';

const AddReview = ({ route }) => {
  const { data } = route.params;
  const [rating, setRating] = useState(0);
  const [reviewText, setReviewText] = useState('');
  const { cachedData, setCachedData } = useContext(AppContext);

  const handleStarPress = (newRating) => {
    setRating(newRating);
  };

  const handleChangeText = (newText) => {
    setReviewText(newText);
  };

  const toRating = () => {
    const newReview = {
      id: data.code,
      rating: rating,
      reviewText: reviewText,
    };

    const updatedData = cachedData.map((item) => {
      if (item.code === data.code) {
        return { ...item, rating: rating, reviewText: reviewText };
      }
      return item;
    });

    setCachedData(updatedData);

    setRating(0);
    setReviewText('');
  };
  return (
    <View style={styles.root}>
      <Text style={styles.addReview}>Add Review</Text>
      <Text style={styles.input}>{data.faculty}</Text>
      <Text style={styles.rating}>Your Rating</Text>
      <View >
        <StarRating rating={rating} onStarPress={handleStarPress} />
      </View>
      <TextInput
        placeholder="Review"
        onChangeText={handleChangeText}
        style={styles.input}
      ></TextInput>
      <Button title="Submit" onPress={toRating} />
    </View >
  );
};

const styles = StyleSheet.create({
  root: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: 20,
    justifyContent: 'center',
    alignItems: 'center'
  },
  button: {
    paddingHorizontal: 10,
  },
  addReview: {
    fontSize: 25,
    color: '#444',
    textAlign: 'center',
    margin: 20,
  },
  input: {
    padding: 10,
    marginVertical: 10,
    marginHorizontal: 20,
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 3,
  },
  rating: {
    fontSize: 20,
    color: 'grey',
    textAlign: 'center',
    marginVertical: 40,
  },
  stars: {
    marginBottom: 80,
    flexDirection: 'row',
    justifyContent: 'center',
  },
  starButton: {
    padding: 5,
  },
  submitButton: {
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: '#0066cc',
    borderRadius: 4,
    marginVertical: 10,
    marginHorizontal: 20,
  },
  submitButtonText: {
    fontSize: 18,
    color: '#ffffff',
    textAlign: 'center',
  },
});

export default AddReview;
